import React, {useState} from "react";
import { TextField, Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import "./style.scss";
import axios from 'axios'

function Register() {
  const [user, setUser] = useState({email:'',password:''});

  const onChangeEmail=(e,val)=>{
    setUser({...user,[val]:e.target.value})
}
  const onSubmit=(e)=>{
    e.preventDefault();
    //new user object
    const user = {
        username:this.state.username
    }
    console.log(user)
    axios.post('http://localhost:5000/users/add', user)
        .then(res => console.log(res.data));
    setUser({})
}
  return (
    <div>
      <h1>Register</h1>
      <form onSubmit={onSubmit}>
        <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          id="email"
          label="Email Address"
          autoFocus
          value={user.email}
          onChange={onChangeEmail('email')}
        ></TextField>
        <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          id="password"
          label="Password"
          type="password"
        ></TextField>

        <Button
          className="submit-btn"
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
        >
          Sign In
        </Button>
      </form>
      <p>
        Already a member? <Link to="/login">Log in here</Link>
      </p>
    </div>
  );
}

export default Register;
