import React from "react";
import {Slide} from 'react-slideshow-image'
import 'react-slideshow-image/dist/styles.css'
const images = [
  'https://photos.mandarinoriental.com/is/image/MandarinOriental/paris-2017-home?wid=2880&hei=1280&fmt=jpeg&crop=9,336,2699,1200&anchor=1358,936&qlt=75,0&fit=wrap&op_sharpen=0&resMode=sharp2&op_usm=0,0,0,0&iccEmbed=0&printRes=72',
  'https://uceap.universityofcalifornia.edu/sites/default/files/marketing-images/program-page-images/french-in-paris-glance.jpg'

];

function Home() {
  
  return (
    <div>
      <h1>Home Page</h1>
      <Slide arrow={false}>
         
            <div style={{'backgroundImage': `url(${images[0]})`, 'height':"500px"}}/>
              
          
            <div style={{'backgroundImage': `url(${images[1]})`, 'height':"500px"}}/>
            
      
          
        </Slide>
    </div>
  );
}

export default Home;
